        handleSubmit();
    },[])
	return (
		<div>
			<FoodList
				searchString={searchString}
				handleSubmit={handleSubmit}
				handleChange={handleChange}
			/>
            <h2>{setSearchString.hits}</h2>

		</div>
	);

}

export default FoodSearch;
